import logo from './logo.svg';
import './App.css';
import { json } from 'stream/consumers';


function App() {
  const socket = useRef<WebSocket>();
  const [connected, setConnected] = useState(false)
  const [coords, setCoords] = useState({ screenX: 0, screenY: 0 })

  useEffect(() => {
    connect()
    // handleMouseEvents()
  }, [])

